package ru.tatyana.demo.controller;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.MediaType;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.RestController;import ru.tatyana.demo.businessLayer.impl.BarChartServiceBLImpl;import ru.tatyana.demo.model.BarChart;import java.util.List;/** * Created by Tatyana on 14.04.2016. */@RestController@RequestMapping(value = {"api/barchart"})public class BarChartController {    @Autowired    BarChartServiceBLImpl barChartServiceBLImpl;    @RequestMapping(value = {"/bymonth"}, method = RequestMethod.GET, produces = MediaType.APPLICATION_JSON_VALUE)    public List<BarChart> getDataByMonth() {        return barChartServiceBLImpl.getDataByMonth();    }    @RequestMapping(value = {"/byyear"}, method = RequestMethod.GET, produces = MediaType.APPLICATION_JSON_VALUE)    public List<BarChart> getDataByYear() {        return barChartServiceBLImpl.getDataByYear();    }}