package ru.tatyana.demo.controller;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.MediaType;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.RestController;import ru.tatyana.demo.businessLayer.impl.PyramidServiceBLImpl;import ru.tatyana.demo.model.Pyramid;import java.util.List;/** * Created by Tatyana on 14.04.2016. */@RestController@RequestMapping(value = {"api/pyramid"})public class PyramidController {    @Autowired    PyramidServiceBLImpl pyramidServiceBLImpl;    @RequestMapping(value = {"/bymonth"}, method = RequestMethod.GET, produces = MediaType.APPLICATION_JSON_VALUE)    public List<Pyramid> getDataByMonth() {        return pyramidServiceBLImpl.getDataByMonth();    }    @RequestMapping(value = {"/byyear"}, method = RequestMethod.GET, produces = MediaType.APPLICATION_JSON_VALUE)    public List<Pyramid> getDataByYear() {        return pyramidServiceBLImpl.getDataByYear();    }}