package ru.tatyana.demo.restController;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.MediaType;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.RestController;import ru.tatyana.demo.businessLayer.PyramidBL;import ru.tatyana.demo.model.Pyramid;import java.util.List;/** * Created by Tatyana on 14.04.2016. */@RestController@RequestMapping(value = {"api"})public class PyramidController {    @Autowired    PyramidBL pyramidBL;    @RequestMapping(value = {"/pyramid/bymonth"}, method = RequestMethod.GET, produces = MediaType.APPLICATION_JSON_VALUE)    public List<Pyramid> getDataByMonth() {        return pyramidBL.getDataByMonth();    }    @RequestMapping(value = {"/pyramid/byyear"}, method = RequestMethod.GET, produces = MediaType.APPLICATION_JSON_VALUE)    public List<Pyramid> getDataByYear() {        return pyramidBL.getDataByYear();    }}